import React, {useState} from "react";
import classes from "./Form.module.css";

const Form = (props) => {
const [userInput,setUserInput]=useState({
    'current-savings':'',
    'yearly-contribution':'',
    'expected-return':'',
    duration:'',
});

const getInputValue=(input,value)=>
{
setUserInput((prevState)=>
{
    return {...prevState,[input]:value};
})
}
    
    const submitHandler = (e)=>
    {
        e.preventDefault();
    }
    const resetHandler=()=>{
        setUserInput('');
    }

  return (
    <form onSubmit={submitHandler} className={classes.form}>
      <div className={classes["input-group"]}>
        <p>
          <label  htmlFor="current-savings">Current Savings ($)</label>
          <input type="number" onChange={(e)=>getInputValue('current-savings',e.target.value)} value={userInput['current-savings']} id="current-savings" />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input type="number" onChange={(e)=>getInputValue('yearly-contribution',e.target.value)} value={userInput['yearly-contribution']} id="yearly-contribution" />
        </p>
      </div>
      <div className={classes["input-group"]}>
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input type="number" onChange={(e)=>getInputValue('expected-return',e.target.value)} value={userInput["expected-return"]} id="expected-return" />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input type="number" onChange={(e)=>getInputValue('duration',e.target.value)} value={userInput.duration} id="duration" />
        </p>
      </div>
      <p className={classes.actions}>
        <button type="reset" onClick={resetHandler} className={classes.buttonAlt}>
          Reset
        </button>
        <button type="submit" className={classes.button}>
          Calculate
        </button>
      </p>
    </form>
  );
};

export default Form;
